import { useState } from "react";

export default function CreateRestaurantPage() {

    const [title, setTitle] = useState('');
    const [description, setDescription]  = useState('');
    const [foodType, setFoodType] = useState('');
    const [location, setLocation] = useState('');

    async function createNewRestaurant(ev) {
        const data = new FormData();
        data.set('title', title);
        data.set('description', description);
        data.set('foodType', foodType);
        data.set('location', location )

        ev.preventDefault();
        const response = await fetch('http://localhost:4000/post', {
            method: 'POST',
            body: data,
        });
        if (response.ok) {
            const responseData = await response.json(); // Преобразование ответа от сервера в JSON
            console.log(responseData); // Вывод ответа от сервера в консоль
        }
    }

    return (
        <form onSubmit={createNewRestaurant}>
            <input 
                type="title" 
                placeholder={"Название"} 
                value={title}
                onChange={ev => setTitle(ev.target.value)}/>
            <input 
                type="description" 
                placeholder={"Описание"}
                value={description}
                onChange={ev => setDescription(ev.target.value)}/>   
            <input 
                type="foodType" 
                placeholder={"Кухня"}
                value={foodType}
                onChange={ev => setFoodType(ev.target.value)}/> 
            <input 
                type="location" 
                placeholder={"Локация"}
                value={location}
                onChange={ev => setLocation(ev.target.value)}/>   
            <button style={{marginTop: '5px'}}>Добавить ресторан</button>
        </form>
    )
}
