import React, { useState } from 'react';

export default function CreateRestaurantPage() {
    const [formData, setFormData] = useState({
        title: '',
        description: '',
        foodType: '',
        location: ''
    });
    const [formVisible, setFormVisible] = useState(false);

    async function createNewRestaurant(ev) {
        ev.preventDefault();
        try {
            const response = await fetch('http://localhost:4000/post', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(formData)
            });

            if (response.ok) {
                console.log('Restaurant created successfully!');
                setFormVisible(false); // скрываем форму после успешного создания ресторана
            } else {
                console.error('Failed to create restaurant.');
            }
        } catch (error) {
            console.error('Error:', error);
        }
    }

    function handleInputChange(event) {
        const { name, value } = event.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value
        }));
    }

    function toggleFormVisibility() {
        setFormVisible(!formVisible); // изменяем состояние для отображения/скрытия формы
    }

    return (
        <div>
            {/* Кнопка для открытия/закрытия формы */}
            <button onClick={toggleFormVisibility}>
                {formVisible ? 'Скрыть форму' : 'Добавить ресторан'}
            </button>

            {/* Форма */}
            {formVisible && (
                <form className='createRestaurantForm' onSubmit={createNewRestaurant}>
                    <h1>Добавить Ресторан</h1>
                    <input
                        type="text"
                        name="title"
                        placeholder="Название"
                        value={formData.title}
                        onChange={handleInputChange}
                    />
                    <input
                        type="text"
                        name="description"
                        placeholder="Описание"
                        value={formData.description}
                        onChange={handleInputChange}
                    />
                    <input
                        type="text"
                        name="foodType"
                        placeholder="Кухня"
                        value={formData.foodType}
                        onChange={handleInputChange}
                    />
                    <input
                        type="text"
                        name="location"
                        placeholder="Локация"
                        value={formData.location}
                        onChange={handleInputChange}
                    />
                    <button style={{ marginTop: '5px' }}>Добавить ресторан</button>
                </form>
            )}
        </div>
    );
}
